drop table if exists content_types;
create table content_types
(
    id                int            not null primary key,
    machine_name      varchar(150)   not null,
    major_version     int            not null,
    minor_version     int            not null,
    patch_version     int            not null,
    h5p_major_version int            not null,
    h5p_minor_version int            not null,
    title             nvarchar(100)  not null,
    summary           nvarchar(200)  not null,
    description       nvarchar(1000) not null,
    icon              varchar(200)   not null,
    created_at        int            not null,
    popularity        int            not null,
    screenshots       varchar(2000)  not null,
    example           varchar(300)   not null,
    tutorial          varchar(200)   not null,
    keywords          varchar(200)   not null,
);

insert into content_types (id, machine_name, major_version, minor_version, patch_version, h5p_major_version,
                           h5p_minor_version, title, summary, description, icon, created_at, popularity, screenshots,
                           example, tutorial, keywords)
