--         (30, N'H5P.AudioRecorder', 1, 0, 20, 1, 12, N'Audio Recorder', N'Create an audio recording', N'An HTML5 audio recorder. Record your voice and play back or download a .wav file of your recording. Use the H5P plugin to create the H5P Audio Recorder to your Drupal, Wordpress or Moodle site. ', N'https://h5p.org/sites/default/files/icon_1.svg', 1493377972, 32, N'[{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/audio-recorder-1.png?itok=vAcuqLSA","alt":"Start screen for audio recorder"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/audio-recorder-2.png?itok=qoLWgAaq","alt":"Recording screen for audio recorder"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/audio-recorder-3.png?itok=EnH5rwO4","alt":"Finish screen for audio recorder"}]', N'https://h5p.org/audio-recorder', N'', N'["audio","recorder"]'),
--         (31, N'H5P.SpeakTheWords', 1, 3, 11, 1, 14, N'Speak the Words', N'Answer a question using your voice (Chrome only)', N'Speak the Words is only supported in browsers that implements the Web Speech API (Chrome browsers, except on iOS). You need a microphone to answer the question. Ask a question to users and make them answer using their voice. You can choose multiple correct answers. The user will be able to see what his words were interpreted as, and how close it was to the correct answers.', N'https://h5p.org/sites/default/files/speak%20the%20words_0.svg', 1493897505, 25, N'[{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/QuestionPagev2.png?itok=WYSV9ON0","alt":"Question text and answer with microphone screen"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/ShowSolutionPagev2_0.png?itok=vUTAG0RT","alt":"Interpreted and correct answers screen"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/CorrectSolutionPage.png?itok=poT-tQ6Z","alt":"Correct answer screen"}]', N'https://h5p.org/node/72682', N'', N'["language","translation","voice","recognition","speech","mic","microphone"]'),
--         (32, N'H5P.Agamotto', 1, 5, 2, 1, 19, N'Agamotto (Image Blender)', N'Present a sequence of images and explanations', N'Present a sequence of images that people are supposed to look at one after the other, e.g. photos of an item that changes over time, schematics or maps that are organized in different layers or images that reveal more and more details.', N'https://h5p.org/sites/default/files/icon_2.svg', 1494963576, 33, N'[{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/agamotto-02.png?itok=xlA7OqvC","alt":"Agamotto shown as a layered map"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/agamotto-01.png?itok=nxg6d8eA","alt":"Agamotto transforming between different seasons"}]', N'https://h5p.org/node/79243', N'https://h5p.org/agamotto-tutorial', N'["image","images","transition"]'),
--         (33, N'H5P.ImageSequencing', 1, 1, 0, 1, 12, N'Image Sequencing', N'Place images in the correct order', N'A free HTML5 based image sequencing content type that allows authors to add a sequence of their own images (and optional image description) to the game in a particular order. The order of the images will be randomized and players will have to reorder them based on the task description. ', N'https://h5p.org/sites/default/files/icon_3.svg', 1503485910, 27, N'[{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/Selection_386.png?itok=HzsvJWBU","alt":"Initial screen"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/Selection_387.png?itok=m28lDPwX","alt":"Drag and drop images"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/Selection_388.png?itok=IGV1PN-F","alt":"Result screen when one or more is incorrect"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/Selection_389.png?itok=VZQIfLEN","alt":"Result screen when all image are correctly ordered"}]', N'https://h5p.org/content-types/image-sequencing', N'https://h5p.org/tutorial-image-sequencing', N'["image","sequence","order"]'),
--         (34, N'H5P.Flashcards', 1, 5, 19, 1, 4, N'Flashcards', N'Create stylish and modern flashcards', N'This content type allows authors to create a single flash card or a set of flashcards, where each card has images paired with questions and answers. Learners are required to fill in the text field and then check the correctness of their solution.', N'https://h5p.org/sites/default/files/icon_4.svg', 1503910719, 13, N'[{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/flashcards-01_0.png?itok=5xeC6hkY","alt":"Learner has not given any answer yet"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/flashcards-02_0.png?itok=DQTjS65M","alt":"Correct answer given"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/flashcards-03_0.png?itok=833omhR4","alt":"Wrong answer given"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/flashcards-04.png?itok=6S_kGNS4","alt":"Summary"}]', N'https://h5p.org/flashcards', N'https://h5p.org/tutorial-flashcards', N'["cards","animation"]'),
--         (35, N'H5P.SpeakTheWordsSet', 1, 1, 10, 1, 14, N'Speak the Words Set', N'Create a series of questions answered by speech (Chrome only)', N'Speak the Words Set is only supported in browsers that implement the Web Speech API (Chrome browsers, except on ios). You need a microphone to answer the question. Create a set of questions that learners can answer using their voice. you can choose multiple correct answers. The user will be able to see what his words were interpreted as, and how close it was to the correct answers.', N'https://h5p.org/sites/default/files/speak%20the%20words%20set.svg', 1506503229, 23, N'[{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-01.png?itok=92yvDMEt","alt":"An image showing the start screen"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-02.png?itok=SSpDaEf6","alt":"An image showing the question screen"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-03.png?itok=QRR1KWn3","alt":"An image showing the recording taking place"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-04.png?itok=3vjLOvF3","alt":"An image showing a correct answer is given"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-05.png?itok=74miXXWG","alt":"An image showing the solution view"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-06.png?itok=UBAPLNJI","alt":"An image showing the finish screen"}]', N'https://h5p.org/speak-the-words-set', N'', N'["language","translation","voice","quiz","set","recognition","mic","microphone"]'),
--         (36, N'H5P.ImageSlider', 1, 1, 1, 1, 14, N'Image Slider', N'Easily create an Image Slider', N'Present your images in an appealing way with ease. Authors just have to upload images and provide alternative texts for the images. \r\n\r\nThe next two images are always preloaded so switching between images will usually be snappy with no delay for loading the next image.\r\n\r\nImages may be experienced as part of the page or in full-screen mode. When used as part of the page the system will pick a fixed aspect ratio depending on the images being used. Authors may decide to handle aspect ratios differently.', N'https://h5p.org/sites/default/files/icon_5.svg', 1508146690, 17, N'[{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-01_0.png?itok=PRJNeyt3","alt":"Preview using full width"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-02_0.png?itok=NXMcjo0p","alt":"Preview with a portrait image"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-03_0.png?itok=jqQysnpP","alt":"Full-screen preview"}]', N'https://h5p.org/image-slider', N'https://h5p.org/tutorial-image-slider', N'["image","images","slider","carousel"]'),
--         (37, N'H5P.Essay', 1, 2, 3, 1, 13, N'Essay', N'Create Essay with instant feedback', N'In this content type, the author defines a set of keywords that represent crucial aspects of a topic. These keywords are matched against a text that students have composed and can be used to immediately provide feedback - either suggesting to revise certain topic details if a keyword is missing or, confirming the student''s ideas if the text contains a keyword.\r\n', N'https://h5p.org/sites/default/files/icon_6.svg', 1515752007, 20, N'[{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/essay-01.png?itok=PJmHwrPp","alt":"Initial view"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/essay-02.png?itok=b8u7DK0a","alt":"Showing sample (show solution clicked)"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/essay-03.png?itok=m8BrMhVW","alt":"Feedback given"}]', N'https://h5p.org/content-types/essay', N'https://h5p.org/tutorial-essay', N'["Essay"]'),
--         (38, N'H5P.ImagePair', 1, 4, 0, 1, 14, N'Image Pairing', N'Drag and drop image matching game', N'Image pairing is a simple and effective activity that require learners to match pairs of images. Since it is not required for both images in a pair to be the same, authors are also able to test the understanding of a relation between two different images. ', N'https://h5p.org/sites/default/files/icon_7.svg', 1525354557, 31, N'[{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/image-pairing-01.png?itok=6CRqBI7P","alt":"Initial screen"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/image-pairing-02.png?itok=ZgStVtVj","alt":"After check - with errors"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/image-pairing-03.png?itok=yIpQuIMP","alt":"In action"}]', N'https://h5p.org/image-pairing', N'https://h5p.org/tutorial-image-pairing', N'["image images matching pair"]'),
--         (39, N'H5P.Dictation', 1, 0, 7, 1, 13, N'Dictation', N'Create a dictation with instant feedback', N'You can add audio samples containing a sentence for dictation and enter the correct transcription. Your students can listen to the samples and enter what they have heard in to a text field. Their answers will be evaluated automatically. Several options will allow you to control the exercise''s difficulty. You can optionally add a second audio sample for a sentence that could hold a version spoken slowly. You can also set a limit for how often a sample can be played, define if punctuation should be relevant for scoring, and decide whether small mistakes like typing errors should be counted as no mistake, a full mistake, or just a half mistake.', N'https://h5p.org/sites/default/files/icon_8.svg', 1544698878, 34, N'[{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/dictation-1_0.png?itok=Wu85TuX8","alt":"Initial view"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/dictation-2_0.png?itok=Mg0TRDj5","alt":"Show score"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/dictation-3_0.png?itok=eKpbR2t2","alt":"Showing solution"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/dictation-berries_0.png?itok=m9KR17o8","alt":"A dictation about berries"}]', N'https://h5p.org/dictation', N'https://h5p.org/tutorial-dictation', N'["language","audio"]'),
--         (40, N'H5P.BranchingScenario', 1, 2, 2, 1, 20, N'Branching Scenario (beta)', N'Create dilemmas and self paced learning', N'Branching Scenario allow authors to present the learners with a variety of rich interactive content and choices. The choices the learners make will determine the next content they see. May be used to create dilemmas, serious games, and self-paced learning.', N'https://h5p.org/sites/default/files/icon_9.svg', 1551271593, 37, N'[{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-01_0_0.png?itok=eRgwKqbj","alt":"Screenshot 01"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-01_1.png?itok=mDY0Y_ys","alt":"Screenshot 02"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-01_2.png?itok=YVxss_-c","alt":"Screenshot 03"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-01_3.png?itok=TdTgaM87","alt":"Screenshot 04"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-01_4.png?itok=DPsyysid","alt":"Screenshot 05"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-01_5.png?itok=BrCSMC1L","alt":"Screenshot 06"}]', N'https://h5p.org/branching-scenario', N'https://h5p.org/tutorial-branching-scenario', N'[]'),
--         (41, N'H5P.ThreeImage', 0, 4, 11, 1, 20, N'Virtual Tour (360)', N'Create 360 environments with interactions', N'360 (equirectangular) and normal images may be enriched with interactivities like explanations, videos, sounds, and interactive questions. The images create scenes that also may be linked together to give the user an impression of moving between environments or between different viewpoints within the same environment.', N'https://h5p.org/sites/default/files/360-icon.svg', 1551273005, 40, N'[{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-01_1_0.png?itok=GUmLlxSY","alt":"Screenshot 02"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-01_2_0.png?itok=t5YmuLiK","alt":"Screenshot 03"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/screenshot-01_3_0.png?itok=58i8supV","alt":"Screenshot 04"}]', N'https://h5p.org/virtual-tour-360', N'https://h5p.org/tutorial-virtual-tour', N'[]'),
--         (42, N'H5P.FindTheWords', 1, 4, 4, 1, 20, N'Find the words', N'Grid word search game', N'A free HTML5-based word search activity that allows authors to create a list of words that will be drawn in a grid. The learner''s task is to find and select the words in the grid.', N'https://h5p.org/sites/default/files/icon_10.svg', 1563536197, 28, N'[{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/find-the-words-01.png?itok=O2BgqYiR","alt":"Learners has chosen 5 of 12 correct words"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/find-the-words-02.png?itok=J9s5I6oM","alt":"Show solution mode"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/find-the-words-03.png?itok=vz4BrCJd","alt":"Greek characters"}]', N'https://h5p.org/find-the-words', N'', N'[]'),
--         (43, N'H5P.InteractiveBook', 1, 3, 1, 1, 23, N'Interactive Book', N'Create small courses, books and tests', N'Create small courses, books, or tests. Interactive Book allows authors to combine large amounts of interactive content like interactive videos, questions, course presentations, and more on multiple pages. There is a summary at the end summing up the scores obtained by the learner throughout the book.', N'https://h5p.org/sites/default/files/icon_11.svg', 1596534830, 42, N'[{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/interactive-book-01_1.png?itok=Pe4eW1mb","alt":"Interactive book page"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/interactive-book-01_2.png?itok=Vz7aDDvh","alt":"Interactive book summary page"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/interactive-book-01_3.png?itok=nBDvwn__","alt":"Footer"},{"url":"https://h5p.org/sites/default/files/styles/h5p-content-type-hub-screenshot/public/interactive-book-01_4.png?itok=FFNO5sLk","alt":"Start screen (book cover)"}]', N'https://h5p.org/content-types/interactive-book', N'', N'["book","course"]'),
